import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";
import axios from "axios";

import contactsService from "./services/contacts";

import Search from "./components/Search";
import Form from "./components/Form";
import Contacts from "./components/Contacts";

const App = () => {
  const [contacts, setContacts] = useState([]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [query, setQuery] = useState("");

  // Gell all
  useEffect(() => {
    contactsService
      .getAll()
      .then(response => {
        setContacts(response.data)
      })
  }, []);

  const handleName = (e) => {
    setNewName(e.target.value);
  };

  const handleNumber = (e) => {
    setNewNumber(e.target.value);
  };

  // Add
  const addContact = (e) => {
    //e.preventDefault();

    const newContact = {
      name: newName,
      number: newNumber,
    };

    // Checking if contact exists 
    contacts.filter((contact) => contact.name === newContact.name).length > 0
      ? alert(`${newContact.name} already exists!`)
      : setContacts(contacts.concat(newContact));

    contactsService
    .add(newContact)
    .then(response => {
      setContacts(contacts.concat(response.data));
    })

    setNewName("");
    setNewNumber("");
  };

  const deleteContact = (id) => {
    contactsService
    .deleteContact(id)
    .then(response => {
      const newArr = contacts.filter(contact => contact.id !== id );
      setContacts(newArr);
    })
  };

  const handleQuery = (e) => {
    setQuery(e.target.value);
  };

  const searchResults = contacts.filter(
    (contact) => contact.name.toLowerCase() === query.toLowerCase()
  );

  return (
    <div>
      <h1>Phonebook</h1>
      <h2>Search contacts</h2>
        <Search handleQuery={handleQuery} searchResults={searchResults} />  
      <h2>Add contact</h2>
      <Form
        onSubmit={addContact}
        handleName={handleName}
        handleNumber={handleNumber}
      />
      <h2>Contacts</h2>
      <Contacts contacts={contacts} deleteContact={deleteContact} />
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
