import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";

import contactsService from "./services/contacts";

import Search from "./components/Search";
import Form from "./components/Form";
import Contacts from "./components/Contacts";

const App = () => {
  const [contacts, setContacts] = useState([]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [query, setQuery] = useState("");

  // Gell all
  useEffect(() => {
    contactsService.getAll().then((response) => {
      setContacts(response.data);
    });
  }, []);

  const handleName = (e) => {
    setNewName(e.target.value);
  };

  const handleNumber = (e) => {
    setNewNumber(e.target.value);
  };

  // Add contact
  const addContact = (e) => {
    e.preventDefault();

    // Create new contact
    const newContact = {
      name: newName,
      number: newNumber,
    };

    setNewName("");
    setNewNumber("");

    // Replace number if contact exists
    if (
      contacts.filter((contact) => contact.name === newContact.name).length > 0
    ) {
      alert(`${newContact.name} already exists! Update number?`);

      // Find index of existing contact
      const index = contacts.findIndex(
        (contact) => contact.name === newContact.name
      );
      console.log(index);

      // Update contact
      contactsService.update(index, newContact).then((response) => {
        console.log(response);
        console.log(response.data);
        console.log(contacts);
        setContacts(contacts.concat(response.data));
        console.log(contacts);
      });

    } else {
      // Add
      contactsService.add(newContact).then((response) => {
        setContacts(contacts.concat(response.data));
      });
    }

    contactsService.getAll().then((response) => {
      setContacts(response.data);
    });


  };

  // Delete contact
  const deleteContact = (id) => {
    contactsService.deleteContact(id).then((response) => {
      const newArr = contacts.filter((contact) => contact.id !== id);
      setContacts(newArr);
    });
  };

  // Search
  const handleQuery = (e) => {
    setQuery(e.target.value);
  };

  const searchResults = contacts.filter(
    (contact) => contact.name.toLowerCase() === query.toLowerCase()
  );

  return (
    <div>
      <h1>Phonebook</h1>
      <h2>Search contacts</h2>
      <Search handleQuery={handleQuery} searchResults={searchResults} />
      <h2>Add contact</h2>
      <Form
        onSubmit={addContact}
        handleName={handleName}
        handleNumber={handleNumber}
      />
      <h2>Contacts</h2>
      <Contacts contacts={contacts} deleteContact={deleteContact} />
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
