import axios from 'axios'
const baseUrl = 'http://localhost:3002/contacts'

const getAll = () => {

    return axios.get(baseUrl)
  }

  const add = newContact => {
    return axios.post(baseUrl, newContact)
  }



  export default { 
    getAll: getAll, 
    add: add
  }
