import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";
import contactsService from "./services/contacts";

import Search from "./components/Search";
import Form from "./components/Form";
import Contacts from "./components/Contacts";
import Notification from "./components/Notification";

const App = () => {
  const [contacts, setContacts] = useState([]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [query, setQuery] = useState("");
  const [message, setMessage] = useState("");
  const [err, setErr] = useState(false);

  // Gell all
  useEffect(() => {
    contactsService.getAll().then((response) => {
      setContacts(response.data);
    });
  }, []);

  const handleName = (e) => {
    setNewName(e.target.value);
  };

  const handleNumber = (e) => {
    setNewNumber(e.target.value);
  };

  // Add contact
  const addContact = (e) => {
    e.preventDefault();

    // Create new contact object
    const newContact = {
      name: newName,
      number: newNumber,
    };

    // Check if contact exists
    const existingContact = contacts.find(
      (contact) => contact.name === newContact.name
    );

    // If true
    if (existingContact) {
      alert(`${newContact.name} already exists! Update number?`);
      const updatedContact = { ...existingContact, number: newContact.number };

      // Find index of existing contact
      const index = contacts.findIndex(
        (contact) => contact.name === updatedContact.name
      );

      // Update existing contact
      contactsService.update(index, updatedContact).then((response) => {
        setContacts(
          contacts.map((contact, i) => (i !== index ? contact : response.data))
        );
        setMessage(
          `Updated ${updatedContact.name}'s number`
        )
        setTimeout(() => {
          setMessage(null)
        }, 3000)
      })

      // Handle error
      .catch(error => {
        setErr(true);
        console.log(err);
        setMessage(
          `Oops, ${updatedContact.name} has already been deleted from the server `
        )
        setTimeout(() => {
          setMessage(null)
        }, 3000)
         setContacts(contacts.filter(contact => contact.name !== updatedContact.name));
         setErr(false);
       })
      ;

    } else {
      // Add new contact
      contactsService.add(newContact).then((response) => {
        setContacts(contacts.concat(response.data));
        setMessage(
          `Added ${newContact.name}`
        )
        setTimeout(() => {
          setMessage(null)
        }, 3000)
      });

    }

    // Clear inputs
    setNewName("");
    setNewNumber("");
  };

  // Delete contact
  const deleteContact = (id) => {
    contactsService.deleteContact(id).then((response) => {
      const newArr = contacts.filter((contact) => contact.id !== id);
      setContacts(newArr);
    });
  };

  // Search
  const handleQuery = (e) => {
    setQuery(e.target.value);
  };

  const searchResults = contacts.filter(
    (contact) => contact.name.toLowerCase() === query.toLowerCase()
  );

  return (
    <div>
      <h1>Phonebook</h1>
      <h2>Search contacts</h2>
      <Search handleQuery={handleQuery} searchResults={searchResults} />
      <h2>Add contact</h2>
      <Form
        onSubmit={addContact}
        name={newName}
        number={newNumber}
        handleName={handleName}
        handleNumber={handleNumber}
      />
      <h2>Contacts</h2>
      <Notification message={message} err={err}/>
      <Contacts contacts={contacts} deleteContact={deleteContact} />
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
