import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";

import Search from "./components/Search";
import Form from "./components/Form";
import Contacts from "./components/Contacts";

const App = () => {
  const [contacts, setContacts] = useState([
    { name: "Pekka", phone: "111-111"},
    { name: "Kirsi", phone: "222-222"},
    { name: "Anna", phone: "333-333"},
  ]);

  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [query, setQuery] = useState("");

  const handleName = (e) => {
    const newName = e.target.value;

    if (contacts.includes(newName)) {
      console.log("yes")
    } else  {
      console.log("no")
    }
    //setNewName(newName);
  };

  const handleNumber = (e) => {
    setNewNumber(e.target.value);
  };

  const addContact = (e) => {
    e.preventDefault();
    const newContact = {
      name: newName,
      phone: newNumber,
    };

    if (contacts.indexOf(newContact) === -1) return 
    setNewName("");
    setNewNumber("");
  };

  const handleQuery = (e) => {
    setQuery(e.target.value);
  };

  const searchResults = contacts.filter((contact) => contact.name === query);

  return (
    <div>
      <h1>Phonebook</h1>
      <h2>Search contacts</h2>
      <Search handleQuery={handleQuery} searchResults={searchResults} />
      <h2>Add contact</h2>
      <Form
        onSubmit={addContact}
        handleName={handleName}
        handleNumber={handleNumber}
      />
      <h2>Contacts</h2>
      <Contacts contacts={contacts} />
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
