import React from 'react'

const Notification = ({message, err}) => {

  const success = {
    color: "green",
    border: "1px solid green"

  };

  const fail = {
    color: "red",
  };


    if (message === null) {
        return null
      }

      return (
        <h3 style={err ? fail : success }>
          {message}
        </h3>
      )
}

export default Notification;
